import fetch from 'node-fetch';
import sharp from 'sharp';

export default async (req, res) => {
    const { username, size, color, device } = req.query;
	
    if (!username) {
        return res.status(400).json({ error: 'Username is required' });
    }

    const avatarSize = size ? size : 80;
    const resizedAvatarSize = avatarSize * 0.9;
    const padding = (avatarSize - resizedAvatarSize) / 2;

    const avatarUrl = `https://avatars.githubusercontent.com/${username}?s=${avatarSize}`;

    // Base64 image to be used as the background: violet
    const base64Violet = '';

    // Base64 image to be used as the background: grey
    const base64Grey = '';
	
    let backgroundImageBuffer;
    if (color === 'violet') {
        backgroundImageBuffer = Buffer.from(base64Violet, 'base64');
    } else if (color === 'grey') {
        backgroundImageBuffer = Buffer.from(base64Grey, 'base64');
    } else {
        return res.status(400).json({ error: 'Invalid color specified' });
    }

    try {
        
        const response = await fetch(avatarUrl);

        if (!response.ok) {
            throw new Error('GitHub user not found');
        }

        const buffer = await response.buffer();

        const image = sharp(buffer);
        const metadata = await image.metadata();

        let processedImage;
        if (metadata.format === 'png') {
            processedImage = await image
                .jpeg()
                .toBuffer();
        } else {
            processedImage = buffer;
        }

        const svgMask = `
            <svg width="${resizedAvatarSize}" height="${resizedAvatarSize}">
                <circle cx="${resizedAvatarSize / 2}" cy="${resizedAvatarSize / 2}" r="${resizedAvatarSize / 2}" />
            </svg>
        `;

        const roundedAvatar = await sharp(processedImage)
            .resize(parseInt(resizedAvatarSize), parseInt(resizedAvatarSize))
            .composite([{
                input: Buffer.from(svgMask),
                blend: 'dest-in'
            }])
            .png()
            .toBuffer();

        const compositedImage = await sharp(backgroundImageBuffer)
            .resize(parseInt(avatarSize), parseInt(avatarSize))
            .composite([{ input: roundedAvatar, top: parseInt(padding), left: parseInt(padding) }])
            .png()
            .toBuffer();

        const finalImage = await sharp(compositedImage)
            .jpeg()
            .toBuffer();

        res.setHeader('Content-Type', 'image/jpeg');
        res.send(finalImage);
    } catch (error) {
        res.status(500).json({ error: error.message });
    }
};